<script lang="ts">    import {Button, LogoIcon, ThemeToggle} from '$lib';	    // AUTHENTICATION CONTENT STATE //    let authState = $state<'register' | 'login'>('register');    const AUTH_META = {        register: {            title: "Register - Geni",            description: "Create your Geni account in seconds",			section: "Register card",        },        login: {            title: "Login - Geni",            description: "Welcome back. Let's get you signed in.",            section: "Login card",        }    }    let isRegister = $derived(authState === 'register');    let currentMeta = $derived(AUTH_META[authState]);    function toggleAuthState() {        authState = isRegister ? 'login' : 'register';    }</script><svelte:head>	<!-- PAGE TITLE AND DESCRIPTION	-->	<title>{currentMeta.title}</title>	<meta name="description" content={currentMeta.description} /></svelte:head><main	class="flex flex-col justify-start items-center w-full min-h-screen h-fit	sm:p-8 background-gradient">	<!-- [MOBILE] GENI LOGO -->	<a		href="https://geni.health"	   	class="sm:hidden flex flex-row items-center justify-center w-fit		gap-2"	>		<LogoIcon class="h-10 w-10 "/>		<h2 class="font-semibold select-none">Geni</h2>	</a>	<!-- REGISTER / LOGIN CARD -->	<section		aria-label={currentMeta.section}		class="flex flex-col justify-start items-center w-full max-w-md h-fit		p-6 sm:p-8"	>		<!-- LOGO -->		<a			href="https://geni.health"			class="hidden sm:flex flex-row items-center justify-center w-fit			gap-2"		>			<LogoIcon class="h-10 w-10"/>			<h2 class="font-semibold select-none">Geni</h2>		</a>		<!-- H2 -->		<p class="">			<Button				onclick={toggleAuthState}				variant="link"				size="no_padding"				class="text-accent-foreground cursor-pointer"			>				Create an account			</Button>		</p>				<ThemeToggle />	</section>		<!-- AI ORB --></main>